import React, { useState, useEffect } from 'react';
import axios from 'axios';
import logoimage from './synchrony-logo-1.png'; // Ensure this is the correct path to your logo
import './Interviews.css';

const NewInterview = () => {
  const [interviewList, setInterviewList] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Fetch interviews from the backend
    axios.get('https://your-backend-endpoint/interviews')
      .then((response) => {
        setInterviewList(response.data);
      })
      .catch((error) => {
        console.error('Error fetching interviews:', error);
      });
  }, []);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Function to render each interview row
  const renderInterviewRow = (interview) => {
    return (
      <tr key={interview.id}>
        <td><input type="checkbox" /></td>
        <td>{interview.applicantName}</td>
        <td>{interview.interviewerName}</td>
        <td>{interview.dateOfInterview}</td>
      </tr>
    );
  };

  return (
    <div className="portal-container">
      <header className="portal-header">
        <img src={logoimage} alt="Company Logo" className="logo-image" />
        <nav className="portal-nav">
          <a href="/interviews">Interviews</a>
          <a href="/new-interview" className="active">New Interview</a>
          <a href="/edit-templates">Edit Templates</a>
          <a href="/data-analysis">Data Analysis</a>
        </nav>
      </header>

      <main className="portal-main">
        <div className="portal-main-header">
          <h1>Synchrony Interviews</h1>
          <div className="search-container">
            <input
              type="text"
              placeholder="Search by interviewer, applicant, or date"
              value={searchQuery}
              onChange={handleSearchChange}
            />
          </div>
        </div>

        <table className="interviews-table">
          <thead>
            <tr>
              <th></th>
              <th>Applicant Name</th>
              <th>Interviewer Name</th>
              <th>Date of Interview</th>
            </tr>
          </thead>
          <tbody>
            {interviewList.map(renderInterviewRow)}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default NewInterview;
