import React, { useState, useEffect } from 'react';
import axios from 'axios';
import logoimage from './synchrony-logo-1.png'; // Ensure this is the correct path to your logo
import './Interviews.css';
import { Link } from 'react-router-dom'; // Assuming you're using react-router for navigation
// Interviews.js



function Interviews() {
  const [interviews, setInterviews] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchInterviews = async () => {
      try {
        const response = await fetch('/api/interviews'); // Your actual API endpoint
        const data = await response.json();
        setInterviews(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchInterviews();
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div className="interviews-container">
      <nav className="navbar">
        <img src={logoImage} alt="Synchrony" className="logo" />
        <div className="nav-links">
          <Link to="/interviews">Interviews</Link>
          <Link to="/new-interview">New Interview</Link>
          <Link to="/data-analysis">Data Analysis</Link>
        </div>
      </nav>
      <input
        type="text"
        placeholder="Search by interviewer, applicant, or date"
        value={searchTerm}
        onChange={handleSearch}
        className="search-bar"
      />
      <table className="interviews-table">
        <thead>
          <tr>
            <th>Applicant Name</th>
            <th>Interviewer Name</th>
            <th>Date of Interview</th>
          </tr>
        </thead>
        <tbody>
          {interviews
            .filter(
              (interview) =>
                interview.applicantName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                interview.interviewerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                interview.date.includes(searchTerm)
            )
            .map((interview) => (
              <tr key={interview.id}>
                <td>{interview.applicantName}</td>
                <td>{interview.interviewerName}</td>
                <td>{interview.date}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}

export default Interviews;
