import React, { useState, useEffect } from 'react';
import axios from 'axios';
import logoImage from './synchrony-logo-1.png'; // Ensure this is the correct path to your logo
import './Interviews.css';
import Navbar from '../Navbar'; // Go up one directory level from Dashboard to components


// Define a simple Table component
function Table({ interviews }) {
  return (
    <table>
      {/* Your table headers and rows */}
    </table>
  );
}

// Interviews.js

  function Interviews() {
    const [interviews, setInterviews] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
      const fetchInterviews = async () => {
        try {
          const response = await fetch('/api/interviews'); // Replace with your actual API endpoint
          const data = await response.json();
          setInterviews(data);
        } catch (error) {
          console.error('Error fetching interviews:', error);
        }
      };

      fetchInterviews();
    }, []);

    const handleSearch = (event) => {
      setSearchTerm(event.target.value);
    };

    return (
        <div className="interviews-container">
          <div className="header">
            <img src={logoImage} alt="Synchrony Logo" className="logo" />
            <Navbar />
          </div>
          <h1 className="recruiting-portal-header">Recruiting Portal</h1>
          <div className="search-container">
            <input
              type="text"
              placeholder="Search by interviewer, applicant, or date"
              value={searchTerm}
              onChange={handleSearch}
              className="search-bar"
            />
          </div>
          <Table interviews={interviews} /> {/* Add the Table component here */}
        </div>
    );
  }
  export default Interviews;
